import React, { createContext, useState, useContext } from 'react';

// 1. Create the Context
const AuthContext = createContext(null);

// 2. Create the Provider Component
// This component will wrap our entire app
export function AuthProvider({ children }) {
  // 3. State to hold the user data
  // We check localStorage to see if the user was already logged in
  const [user, setUser] = useState(() => {
    return JSON.parse(localStorage.getItem('user')) || null;
  });

  // 4. Login function
  const login = (userData) => {
    // We get { name, email } from the login page
    const formattedUser = { name: userData.name, email: userData.email };
    localStorage.setItem('user', JSON.stringify(formattedUser));
    setUser(formattedUser);
  };

  // 5. Logout function
  const logout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  // 6. Value to be passed to all children
  const value = {
    user,
    login,
    logout
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

// 7. Custom Hook - This is what we'll use in our components
export function useAuth() {
  return useContext(AuthContext);
}
