import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // 1. Import useNavigate
import '../App.css';
import api from '../api';

// 2. Import the useAuth hook
import { useAuth } from '../contexts/AuthContext';

function ProviderLoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // 3. Get the login function from the context
  const { login } = useAuth();
  const navigate = useNavigate(); // For redirecting

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);
    try {
      const data = await api.loginProvider({ email, password });
      
      // 4. Call the global login function with the provider data
      login(data.provider); // data.provider is { name: '...', email: '...' }
      
      // 5. Redirect to the homepage
      navigate('/');

    } catch (err) {
      console.error('Provider login failed:', err);
      setError(err.message || 'Invalid email or password.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="auth-card">
      <h2 className="auth-title">Provider Login</h2>
      <p className="auth-subtitle">Access your provider dashboard.</p>
      
      <form onSubmit={handleSubmit} className="auth-form">
        {error && <p className="auth-error">{error}</p>}
        
        <div className="auth-input-group">
          <span className="auth-icon">‚úâÔ∏è</span>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        
        <div className="auth-input-group">
          <span className="auth-icon">üîí</span>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-full auth-button" disabled={isSubmitting}>
          {isSubmitting ? 'Logging In...' : 'Login'}
        </button>
        
        <div className="auth-switch-link">
          Need an account? 
          <Link to="/provider-register">Join as Provider</Link>
        </div>
      </form>
    </div>
  );
}

export default ProviderLoginPage;
