import React, { useState } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import './App.css'; // Global styles

// Import Components & Pages
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import ProviderListPage from './pages/ProviderListPage';
import ProviderProfilePage from './pages/ProviderProfilePage';
import LoginPage from './pages/LoginPage';
import UserRegisterPage from './pages/UserRegisterPage';
import ProviderLoginPage from './pages/ProviderLoginPage';
import ProviderRegisterPage from './pages/ProviderRegisterPage';

function App() {
  const [toast, setToast] = useState(null); // For "Location found"
  const location = useLocation(); // Get current page path

  // Show a "Location found!" toast message
  const showToast = (message) => {
    setToast(message);
    setTimeout(() => setToast(null), 3000); // Hide after 3 seconds
  };

  // Check if we are on the homepage
  const isHomePage = location.pathname === '/';

  return (
    <div className="app-container">
      {/* The Toast Notification */}
      {toast && (
        <div className="toast-notification show">
          <span>üìç</span>
          <span>{toast}</span>
          <button onClick={() => setToast(null)}>‚úñ</button>
        </div>
      )}

      {/* Pass the showToast function to the Navbar */}
      <Navbar showToast={showToast} />

      <main>
        <Routes>
          <Route path="/" element={<HomePage />} />

          {/* All other pages are wrapped in a 'page-content-wrapper'.
            On the homepage, this div is hidden, showing only the background.
            On other pages, it shows the white card for readability.
          */}
          <Route path="/*" element={
            !isHomePage ? (
              <div className="page-content-wrapper">
                <Routes>
                  <Route path="/providers" element={<ProviderListPage />} />
                  <Route path="/providers/:id" element={<ProviderProfilePage />} />
                  <Route path="/login" element={<LoginPage />} />
                  <Route path="/register" element={<UserRegisterPage />} />
                  <Route path="/provider-login" element={<ProviderLoginPage />} />
                  <Route path="/provider-register" element={<ProviderRegisterPage />} />
                </Routes>
              </div>
            ) : null
          } />
        </Routes>
      </main>
    </div>
  );
}

export default App;
