import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // 1. Import Link
import '../App.css';
import api from '../api'; 

function ProviderRegisterPage() {
  // --- States for all provider fields ---
  const [name, setName] = useState('');
  const [email, setEmail] = useState(''); // For login
  const [password, setPassword] = useState(''); // For login
  const [phone, setPhone] = useState(''); // For profile
  const [location, setLocation] = useState(''); // Top-level
  const [description, setDescription] = useState(''); // For profile
  const [serviceCategory, setServiceCategory] = useState('Plumber'); // Top-level
  const [emergencyService, setEmergencyService] = useState(false); // Top-level
  
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      // 2. Build the nested payload object based on your schema
      const newProviderData = {
        name,
        email,
        password,
        serviceCategory,
        location,
        emergencyService,
        profile: {
          description,
          phone,
          email: email // You can re-use the login email as the public contact email
        }
      };

      const data = await api.registerProvider(newProviderData);
      
      alert(`Registration successful for ${data.name}! You can now log in.`);
      // Consider auto-redirecting to login:
      // navigate('/provider-login'); 

    } catch (err) {
      console.error('Registration failed:', err);
      setError(err.message || 'Failed to register. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
        <h2 className="text-2xl font-bold text-center">Join as a Provider</h2>
        
        {error && <p className="text-red-500 text-center">{error}</p>}

        {/* --- All Form Fields --- */}
        <div className="form-group">
          <label htmlFor="name">Business / Full Name</label>
          <input
            type="text" id="name" value={name}
            onChange={(e) => setName(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
            placeholder="e.g. Pro Plumbers Inc."
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Login Email</label>
          <input
            type="email" id="email" value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
            placeholder="This will be your login"
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password" id="password" value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
            minLength="6"
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Public Contact Phone</label>
          <input
            type="tel" id="phone" value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
            placeholder="e.g. 9876543210"
          />
        </div>

        <div className="form-group">
          <label htmlFor="location">Location / City</label>
          <input
            type="text" id="location" value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
            placeholder="e.g. Bangalore"
          />
        </div>

        <div className="form-group">
          <label htmlFor="serviceCategory">Primary Service</label>
          <select
            id="serviceCategory" value={serviceCategory}
            onChange={(e) => setServiceCategory(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          >
            <option>Plumber</option>
            <option>Electrician</option>
            <option>Carpenter</option>
            <option>Painter</option>
            <option>Other</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="description">Profile Description</label>
          <textarea
            id="description" rows="3" value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            placeholder="Tell customers about your services, experience, etc."
          />
        </div>

        <div className="form-group" style={{ display: 'flex', alignItems: 'center' }}>
          <input
            type="checkbox"
            id="emergencyService"
            checked={emergencyService} // Use 'checked' for checkboxes
            onChange={(e) => setEmergencyService(e.target.checked)} // Use 'e.target.checked'
            className="rounded border-gray-300 shadow-sm"
            style={{ width: '1.25rem', height: '1.25rem' }}
          />
          <label htmlFor="emergencyService" className="ml-2 font-medium">
            Offer 24/7 Emergency Service?
          </label>
        </div>

        <button type="submit" className="btn btn-primary w-full" disabled={isSubmitting}>
          {isSubmitting ? 'Creating Account...' : 'Create Account'}
        </button>

        {/* 3. Add the link to the login page */}
        <div className="text-center mt-3">
          <Link to="/provider-login" className="text-blue-600 hover:underline">
            Already have a provider account? Login
          </Link>
        </div>
      </form>
    </div>
  );
}

export default ProviderRegisterPage;
