import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../App.css';
import api from '../api'; 

function ProviderLoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);
    try {
      // This function should be in your api.js file
      const data = await api.loginProvider({ email, password });
      
      alert(`Welcome back, ${data.name}!`);
      
      // In a real app, you'd save the token and user data:
      // localStorage.setItem('providerToken', data.token);
      // localStorage.setItem('provider', JSON.stringify(data.provider));

      // And redirect to a provider dashboard
      // navigate('/provider/dashboard'); 
      navigate('/'); // For now, just go home

    } catch (err) {
      console.error('Provider login failed:', err);
      setError(err.message || 'Invalid email or password.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '500px', margin: '2rem auto' }}>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
        <h2 className="text-2xl font-bold text-center">Provider Login</h2>
        
        {error && <p className="text-red-500 text-center">{error}</p>}

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email" id="email" value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password" id="password" value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-full" disabled={isSubmitting}>
          {isSubmitting ? 'Logging In...' : 'Login'}
        </button>

        <Link 
          to="/provider-register" 
          className="btn btn-secondary w-full" 
          style={{ textDecoration: 'none', display: 'block' }}
        >
          Need an account? Join as Provider
        </Link>
      </form>
    </div>
  );
}

export default ProviderLoginPage;
