import React, { useState } from 'react';
// We'll import App.css, but you might want specific styles later
import '../App.css';

function ProviderRegisterPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [service, setService] = useState('Plumber'); // Default value

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Registering ${name} as a ${service}`);
    // In a real app, you'd send this to your backend
  };

  return (
    <div className="container" style={{ maxWidth: '500px', margin: '2rem auto' }}>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
        <h2 className="text-2xl font-bold text-center">Join as a Provider</h2>
        
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text" id="name" value={name}
            onChange={(e) => setName(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email" id="email" value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="service">What service do you offer?</label>
          <select
            id="service" value={service}
            onChange={(e) => setService(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          >
            <option>Plumber</option>
            <option>Electrician</option>
            <option>Carpenter</option>
            <option>Painter</option>
            <option>Other</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-full">
          Create Account
        </button>
      </form>
    </div>
  );
}

export default ProviderRegisterPage;
