import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../App.css'; // We will add all styles to App.css

function HomePage() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // This function will be called when the center icon is clicked
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // A list of your services
  const services = [
    { name: 'Carpenter', icon: 'carpenter-icon.png', category: 'Carpenter' },
    { name: 'Electrician', icon: 'electrician-icon.png', category: 'Electrician' },
    { name: 'Plumber', icon: 'plumber-icon.png', category: 'Plumber' },
    { name: 'Painter', icon: 'painter-icon.png', category: 'Painter' },
    { name: 'Cleaner', icon: 'cleaner-icon.png', category: 'Cleaner' },
    { name: 'General', icon: 'general-icon.png', category: '' } // Links to all providers
  ];

  return (
    <div className="homepage-container">
      {/* This is the center house icon that you click */}
      <div className="center-house-icon" onClick={toggleMenu}>
        <img src="/images/house_icon.png" alt="All Services" />
      </div>

      {/* This is the big dotted circle.
          It gets the 'open' class when 'isMenuOpen' is true */}
      <div className={`services-circle ${isMenuOpen ? 'open' : ''}`}>
        
        {/* We map over the services array to create the icons */}
        {services.map((service, index) => (
          <Link 
            to={`/providers${service.category ? '?category=' + service.category : ''}`} 
            className="service-icon"
            // We use inline styles to position each icon in a circle
            style={{ '--index': index }} 
            key={service.name}
          >
            <img src={`/images/${service.icon}`} alt={service.name} />
            <span>{service.name}</span>
          </Link>
        ))}

      </div>
    </div>
  );
}

export default HomePage;
