// --- In src/pages/ProviderRegisterPage.js ---

import React, { useState } from 'react';
import '../App.css';
import api from '../api'; // 1. IMPORT YOUR API FILE

function ProviderRegisterPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState(''); // 2. ADD PASSWORD
  const [service, setService] = useState('Plumber');
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // 3. REPLACE YOUR OLD handleSubmit WITH THIS ASYNC FUNCTION
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      // 4. CALL THE (NEW) API FUNCTION
      const newProvider = { name, email, password, serviceCategory: service };
      const data = await api.registerProvider(newProvider);
      
      alert(`Registration successful for ${data.name}! You can now log in.`);
      // You could also redirect them:
      // navigate('/provider-login'); 
      // (You would need to add `const navigate = useNavigate();` at the top)

    } catch (err) {
      console.error('Registration failed:', err);
      setError(err.message || 'Failed to register. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '500px', margin: '2rem auto' }}>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
        <h2 className="text-2xl font-bold text-center">Join as a Provider</h2>
        
        {error && <p className="text-red-500 text-center">{error}</p>}

        {/* ... (keep the name and email fields as they are) ... */}
        
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text" id="name" value={name}
            onChange={(e) => setName(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email" id="email" value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        {/* 5. ADD A PASSWORD FIELD */}
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password" id="password" value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>
        
        {/* ... (keep the service select field as it is) ... */}

        <div className="form-group">
          <label htmlFor="service">What service do you offer?</label>
          <select
            id="service" value={service}
            onChange={(e) => setService(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          >
            <option>Plumber</option>
            <option>Electrician</option>
            <option>Carpenter</option>
            <option>Painter</option>
            <option>Other</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-full" disabled={isSubmitting}>
          {isSubmitting ? 'Creating Account...' : 'Create Account'}
        </button>
      </form>
    </div>
  );
}

export default ProviderRegisterPage;
