import React from 'react';
import { Link } from 'react-router-dom';

/**
 * HomePage.js
 * * - Shows service categories as links (e.g., /providers?category=Plumber).
 * - Includes a button to demonstrate the HTML5 Geolocation API.
 */
function HomePage() {
  
  // HTML5 Geolocation API
  const handleFindNearMe = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          alert(`Found your location!\nLat: ${latitude}\nLon: ${longitude}\n\n(In a real app, we would send this to the API)`);
        },
        () => {
          alert('Could not get your location. Please enable location services.');
        }
      );
    } else {
      alert('Geolocation is not supported by your browser.');
    }
  };

  return (
    <div className="container mx-auto p-6 text-center">
      <h1 className="text-4xl font-bold mb-6">Find Local Services</h1>
      <p className="text-lg text-gray-700 mb-8">
        Get help from trusted professionals in your area.
      </p>

      {/* Service Category Links */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <Link
          to="/providers?category=Plumber"
          className="bg-white p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300"
        >
          <h2 className="text-2xl font-semibold text-blue-700">Plumbers</h2>
          <p className="text-gray-600 mt-2">Leaky faucet? Clogged drain? Find expert plumbers.</p>
        </Link>
        <Link
          to="/providers?category=Electrician"
          className="bg-white p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300"
        >
          <h2 className="text-2xl font-semibold text-green-700">Electricians</h2>
          <p className="text-gray-600 mt-2">Wiring, lighting, and all your electrical needs.</p>
        </Link>
        <Link
          to="/providers"
          className="bg-white p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300"
        >
          <h2 className="text-2xl font-semibold text-gray-700">All Services</h2>
          <p className="text-gray-600 mt-2">Browse our full directory of local pros.</p>
        </Link>
      </div>
      
      {/* Geolocation API Button */}
      <button
        onClick={handleFindNearMe}
        className="bg-indigo-600 text-white font-semibold py-3 px-6 rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-300"
      >
        Find Services Near Me
      </button>
    </div>
  );
}

export default HomePage;
