import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Link is already imported
import '../App.css';
// import api from '../api'; 

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Logging in with: ${email}`);
    // In a real app, you'd call api.loginUser({ email, password })
  };

  return (
    <div className="container" style={{ maxWidth: '500px', margin: '2rem auto' }}>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
        <h2 className="text-2xl font-bold text-center">User Login</h2>
        
        {/* --- Email field --- */}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email" id="email" value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>
        
        {/* --- Password field --- */}
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password" id="password" value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
            required
          />
        </div>

        {/* --- Primary Login Button --- */}
        <button type="submit" className="btn btn-primary w-full">
          Login
        </button>
        
        {/* --- UPDATED LINK (NOW STYLED AS A BUTTON) ---
          We just add the 'btn', 'btn-secondary', and 'w-full' classes to the Link.
          It still works as a link, but it looks like a button.
        */}
        <Link 
          to="/register" 
          className="btn btn-secondary w-full" 
          style={{ textDecoration: 'none', display: 'block' }} // Added styles for good measure
        >
          Don't have an account? Create one
        </Link>
      </form>
    </div>
  );
}

export default LoginPage;
