import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import api from '../api';
/**
 * ProviderListPage.js
 * * - Uses `useSearchParams` to read the category from the URL.
 * - Uses `useState` to store the list of providers.
 * - Uses `useEffect` to fetch data when the component loads.
 * - Uses `async/await` to handle the API call.
 * - Passes data to the `ProviderCard` child component via `props`.
 */
function ProviderListPage() {
  // React Hook: useState
  const [providers, setProviders] = useState([]);
  const [loading, setLoading] = useState(true);

  // React Router Hook: useSearchParams to read URL query
  const [searchParams] = useSearchParams();
  const category = searchParams.get('category');

  // React Hook: useEffect
  useEffect(() => {
    const fetchProviders = async () => {
      setLoading(true);
      try {
        const data = await api.getProviders(category); // Async/Await
        setProviders(data);
      } catch (error) {
        console.error('Failed to fetch providers:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchProviders();
  }, [category]); // Re-run if `category` changes

  const title = category ? `${category}s` : 'All Providers';

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">{title}</h1>
      {loading ? (
        <p>Loading providers...</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {providers.length > 0 ? (
            providers.map((provider) => (
              // Passing `props` to child component
              <ProviderCard key={provider.id} provider={provider} />
            ))
          ) : (
            <p>No providers found for this category.</p>
          )}
        </div>
      )}
    </div>
  );
}

/**
 * ProviderCard (Sub-component)
 * * - Receives `provider` data via `props`.
 * - Renders a summary card for one provider.
 */
function ProviderCard({ provider }) {
  // Calculate average rating
  const avgRating = provider.reviews.length
    ? (provider.reviews.reduce((acc, r) => acc + r.rating, 0) / provider.reviews.length).toFixed(1)
    : 'No reviews';

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:scale-[1.02]">
      <div className="p-6">
        <div className="flex justify-between items-start mb-2">
          <h2 className="text-2xl font-bold text-gray-900">{provider.name}</h2>
          <span className="text-sm font-semibold text-blue-600 bg-blue-100 px-3 py-1 rounded-full">
            {provider.serviceCategory}
          </span>
        </div>
        <p className="text-gray-700 mb-4">{provider.profile.description}</p>
        
        {provider.emergencyService && (
          <span className="text-red-600 font-bold bg-red-100 px-3 py-1 rounded-full text-sm">
            24/7 Emergency Service
          </span>
        )}

        <div className="flex justify-between items-center mt-4 text-gray-600">
          <span>‚≠ê {avgRating} ({provider.reviews.length} reviews)</span>
          <Link
            to={`/providers/${provider.id}`}
            className="text-blue-600 font-semibold hover:underline"
          >
            View Profile &rarr;
          </Link>
        </div>
      </div>
    </div>
  );
}

export default ProviderListPage;
