import React from 'react';
import { Link } from 'react-router-dom';
import '../App.css'; // We will add all styles to App.css

function HomePage() {

  // A list of your services
  const services = [
    { name: 'Carpenter', emoji: 'ü™ö', category: 'Carpenter' },
    { name: 'Electrician', emoji: 'üí°', category: 'Electrician' },
    { name: 'Plumber', emoji: 'üîß', category: 'Plumber' },
    { name: 'Painter', emoji: 'üé®', category: 'Painter' },
    { name: 'Cleaner', emoji: 'üßπ', category: 'Cleaner' },
    { name: 'General', emoji: 'üõ†Ô∏è', category: '' } // Links to all providers
  ];

  return (
    <div className="homepage-container">
      
      {/* This is the center house icon that links to ALL services */}
      <Link to="/providers" className="center-house-icon">
        <span className="center-house-icon-emoji">üè†</span>
        <span>All Services</span>
      </Link>

      {/* This is the big dotted circle */}
      <div className="services-circle">
        
        {/* We map over the services array to create the icons */}
        {services.map((service, index) => (
          <Link 
            to={`/providers${service.category ? '?category=' + service.category : ''}`} 
            className="service-icon"
            // We use inline styles to position each icon in a circle
            style={{ '--index': index }} 
            key={service.name}
          >
            <span className="service-icon-emoji">{service.emoji}</span>
            <span>{service.name}</span>
          </Link>
        ))}

      </div>
    </div>
  );
}

export default HomePage;
