import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

// 1. IMPORT the complete api object from your separate api.js file
import api from '../api'; 
// (All the axios logic and API_URL are now in that file)

/**
 * ProviderProfilePage.js
 * * - Imports the real `api.js` file.
 * - Uses `useParams` to get the provider's `id` from the URL.
 * - Uses `useEffect` to fetch data for that specific provider.
 * - Renders the provider's profile and review list.
 * - Renders the `ReviewForm` component.
 */
function ProviderProfilePage() {
  // React Hook: useState
  const [provider, setProvider] = useState(null);
  const [loading, setLoading] = useState(true);

  // React Router Hook: useParams to get URL param
  const { id } = useParams();

  // React Hook: useEffect
  useEffect(() => {
    const fetchProvider = async () => {
      setLoading(true);
      try {
        // 2. CALL THE REAL API (this will work because api.js has getProviderById)
        const data = await api.getProviderById(id);
        setProvider(data);
      } catch (error) {
        console.error('Failed to fetch provider:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchProvider();
  }, [id]); // Re-run if ID changes

  // Callback function passed to ReviewForm
  const handleReviewSubmit = (updatedProvider) => {
    setProvider(updatedProvider);
  };

  if (loading) return <div className="p-6">Loading profile...</div>;
  if (!provider) return <div className="p-6">Provider not found.</div>;

  // 3. RENDER THE COMPONENT
  // Note: Using `provider._id` because MongoDB uses _id by default
  return (
    <div className="container mx-auto p-6">
      <div className="bg-white rounded-lg shadow-xl p-8 mb-6">
        {/* Service Provider Profile */}
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-4xl font-bold">{provider.name}</h1>
          <span className="text-lg font-semibold text-blue-600 bg-blue-100 px-4 py-2 rounded-full">
            {provider.serviceCategory}
          </span>
        </div>
        {provider.emergencyService && (
          <p className="text-red-600 font-bold bg-red-100 p-3 rounded-md mb-4">
            Available for 24/7 Emergency Service
          </p>
        )}
        <p className="text-xl text-gray-700 mb-6">{provider.profile.description}</p>
        <h3 className="text-2xl font-semibold mb-3">Contact Information</h3>
        <p className="text-gray-800"><strong>Email:</strong> {provider.profile.email}</p>
        <p className="text-gray-800"><strong>Phone:</strong> {provider.profile.phone}</p>
        <p className="text-gray-800"><strong>Location:</strong> {provider.location}</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          {/* Review and Rating System */}
          <h2 className="text-3xl font-semibold mb-4">Reviews</h2>
          <div className="space-y-4">
            {provider.reviews.length > 0 ? (
              provider.reviews.map((review) => (
                <div key={review._id || review.id} className="bg-white p-5 rounded-lg shadow">
                  <div className="flex justify-between items-center mb-1">
                    <span className="font-semibold text-lg">{review.userName}</span>
                    <span className="text-lg">{'⭐'.repeat(review.rating)}</span>
                  </div>
                  <p className="text-gray-700">{review.comment}</p>
                </div>
              ))
            ) : (
              <p className="bg-white p-5 rounded-lg shadow">No reviews yet.</p>
            )}
          </div>
        </div>

        {/* React Form */}
        <div>
          <ReviewForm 
            providerId={provider._id} 
            onReviewSubmit={handleReviewSubmit} 
          />
        </div>
      </div>
    </div>
  );
}

/**
 * ReviewForm (Sub-component)
 * * - A React Form that uses `useState` for controlled inputs.
 * - It also imports and uses the `api` file.
 */
function ReviewForm({ providerId, onReviewSubmit }) {
  const [userName, setUserName] = useState('');
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault(); 
    if (comment.trim() === '' || userName.trim() === '') {
      setError('Please fill out your name and a comment.');
      return;
    }
    
    setIsSubmitting(true);
    setError(null);
    try {
      const newReview = { userName, rating: Number(rating), comment };
      // 4. CALL THE REAL API (this will work because api.js has postReview)
      const updatedProvider = await api.postReview(providerId, newReview);
      
      onReviewSubmit(updatedProvider); // Pass data up to parent
      
      // Reset form
      setUserName('');
      setRating(5);
      setComment('');

    } catch (err) {
      setError('Failed to submit review. Please try again.');
      console.error(err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
      <h3 className="text-xl font-semibold">Leave a Review</h3>
      {error && <p className="text-red-500">{error}</p>}
      <div>
        <label htmlFor="userName" className="block text-sm font-medium text-gray-700">Your Name</label>
        <input
          type="text" id="userName" value={userName}
          onChange={(e) => setUserName(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          required
        />
      </div>
      <div>
        <label htmlFor="rating" className="block text-sm font-medium text-gray-700">Rating</label>
        <select
          id="rating" value={rating}
          onChange={(e) => setRating(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
        >
          <option value="5">5 Stars</option>
          <option value="4">4 Stars</option>
          <option value="3">3 Stars</option>
          <option value="2">2 Stars</option>
          <option value="1">1 Star</option>
        </select>
      </div>
      <div>
        <label htmlFor="comment" className="block text-sm font-medium text-gray-700">Comment</label>
        <textarea
          id="comment" rows="4" value={comment}
          onChange={(e) => setComment(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm"
          required
        />
      </div>
      <button
        type="submit" disabled={isSubmitting}
        className="w-full bg-blue-600 text-white font-semibold py-2 px-4 rounded-lg shadow-md hover:bg-blue-700 disabled:bg-gray-400"
      >
        {isSubmitting ? 'Submitting...' : 'Submit Review'}
      </button>
    </form>
  );
}

export default ProviderProfilePage;
